CREATE INDEX
    ON graticules
    USING GIST (geometry);

CREATE OR REPLACE FUNCTION layer_graticules_3575(bbox geometry)
RETURNS TABLE(geometry geometry, name text) AS $$
   SELECT geometry, name FROM graticules WHERE ST_Intersects(geometry, bbox);

$$ LANGUAGE SQL IMMUTABLE;
